// Arithmetic Operators

let A = 10;
let B = 20;

console.log("Addition:", A + B);         // 30
console.log("Subtraction:", A - B);      // -10
console.log("Multiplication:", A * B);   // 200
console.log("Division:", A / B);         // 0.5
console.log("Remainder:", A % B);        // 10
console.log("Exponent:", A ** 2);        // 100

// Increment
console.log("Pre-increment:", ++A);      // 11
console.log("Post-increment:", B++);     // 20
console.log("After post-increment B:", B); // 21

// Decrement
console.log("Pre-decrement:", --A);      // 10
console.log("Post-decrement:", B--);     // 21
console.log("After post-decrement B:", B); // 20

// Assignment Operators
let A = 10;
let B = 20;

A = B;
console.log("A = B:", A);                // 20

A += 5;
console.log("A += 5:", A);               // 25

A -= 2;
console.log("A -= 2:", A);               // 23

A *= 2;
console.log("A *= 2:", A);               // 46

A /= 2;
console.log("A /= 2:", A);               // 23